/**
 * @swagger
 * tags:
 *   name: Suggestions
 *   description: Endpoints for generating and managing log-based suggestions.
 */

/**
 * @swagger
 * components:
 *   schemas:
 *     Suggestion:
 *       type: object
 *       properties:
 *         id:
 *           type: string
 *           description: Unique ID of the suggestion
 *         idLog:
 *           type: string
 *           description: Associated log ID
 *         report:
 *           type: object
 *           description: Report generated by the suggestion engine
 *           properties:
 *             summary:
 *               type: string
 *               description: Summary of the analysis
 *             details:
 *               type: string
 *               description: Report details
 *         createdAt:
 *           type: string
 *           format: date-time
 *           description: Suggestion creation date
 *     SuggestionInput:
 *       type: object
 *       required:
 *         - id
 *         - owner
 *         - repo
 *       properties:
 *         id:
 *           type: string
 *           description: ID of the log to be processed
 *           example: "64f1c2d9a2f3d6e1a8f7b9c0"
 *         owner:
 *           type: string
 *           description: Repository owner
 *           example: "octocat"
 *         repo:
 *           type: string
 *           description: Repository name
 *           example: "hello-world"
 *         branch:
 *           type: string
 *           description: Repository branch (default `main`)
 *           example: "develop"
 */

/**
 * @swagger
 * /suggestions:
 *   post:
 *     summary: Generate a suggestion report from a log
 *     tags: [Suggestions]
 *     security:
 *       - bearerAuth: []
 *     requestBody:
 *       required: true
 *       content:
 *         application/json:
 *           schema:
 *             $ref: '#/components/schemas/SuggestionInput'
 *     responses:
 *       200:
 *         description: Suggestion created successfully
 *         content:
 *           application/json:
 *             schema:
 *               type: object
 *               properties:
 *                 message:
 *                   type: string
 *                   example: "Suggestion created successfully"
 *                 report:
 *                   $ref: '#/components/schemas/Suggestion'
 *       404:
 *         description: Log not found
 *       500:
 *         description: Error generating report
 */

/**
 * @swagger
 * /suggestions/log/{logId}:
 *   get:
 *     summary: Obtain suggestion reports associated with a log
 *     tags: [Suggestions]
 *     security:
 *       - bearerAuth: []
 *     parameters:
 *       - in: path
 *         name: logId
 *         required: true
 *         schema:
 *           type: string
 *         description: Associated log ID
 *     responses:
 *       200:
 *         description: List of suggestions for the requested log
 *         content:
 *           application/json:
 *             schema:
 *               type: object
 *               properties:
 *                 success:
 *                   type: boolean
 *                   example: true
 *                 result:
 *                   type: object
 *                   properties:
 *                     data:
 *                       type: array
 *                       items:
 *                         $ref: '#/components/schemas/Suggestion'
 *       404:
 *         description: Log not found or no suggestions
 *       500:
 *         description: Server error
 */
